import { useEffect, useReducer } from "react";
import "./App.css";

function App() {
  const initialState = {
    drumTypes: [
      {
        type: "kick",
        source: new Audio(require("./sounds/kick.mp3")),
        key: "q",
      },
      {
        type: "clap",
        source: new Audio(require("./sounds/clap.mp3")),
        key: "w",
      },
      {
        type: "hi-hat",
        source: new Audio(require("./sounds/hihat.mp3")),
        key: "e", //e
      },
      {
        type: "openhat",
        source: new Audio(require("./sounds/openhat.mp3")),
        key: "r", //r
      },
    ],
  };





  useEffect(() => {
    document.addEventListener("keydown", (event) => {
      console.log(event.key);
      let currSound = initialState.drumTypes.filter(
        (drum) => drum.key === event.key
      );
      currSound.map((sound) => {
        return ()=>{
        sound.source.currentTime=0;
        sound.source.play()
        }
      });
    });
  });

  const reducer = () => {};

  const [state, dispatch] = useReducer(reducer, initialState);

  const drumList = initialState.drumTypes.map((drum) => (
    <button key={drum.type} onClick={() => drum.source.play()}>
      {drum.type}
    </button>
  ));

  return (
    <div id="drum-machine">
      <h1>beat player</h1>

      <div id="melody"></div>

      <div id="drums">{drumList}</div>
      <div id="display"></div>
    </div>
  );
}

export default App;
