import { useEffect, useReducer } from "react";
import "./App.css";

const actions = {
  TOGGLESONG : 'toggle-song',
  NEXTSONG: 'next-song',
  PREVSONG: 'prev-song'
}






function App() {
  const initialState = {
    drums: [
      {
        type: "kick",
        source: new Audio(require("./sounds/kick.mp3")),
        key: "q",
        img: require("./images/kick.png"),
      },
      {
        type: "clap",
        source: new Audio(require("./sounds/clap.mp3")),
        key: "w",
        img: require("./images/clap.png"),
      },
      {
        type: "hi-hat",
        source: new Audio(require("./sounds/hihat.mp3")),
        key: "e",
        img: require("./images/hihat.png"),
      },
      {
        type: "openhat",
        source: new Audio(require("./sounds/openhat.mp3")),
        key: "r",
        img: require("./images/openhat.png"),
      },
    ],
    melodies: [
      {
        isPaused: false,
        name: "Horse Training Theme",
        source: require("./sounds/melodyone.mp3")
      },
      {
        isPaused: false,
        name: "Spaceu Industrial",
        source: require("./sounds/melodytwo.mp3")
      }
    ],
    currentSong : 0,
    maxSongs: 1 //set based off of max songs
  };

  const reducer = (state, action) => {
    switch(action.type){
      case actions.TOGGLESONG: {
        console.log(state.currentSong)
        if(state.currentSong<state.maxSongs && action.move === actions.NEXTSONG){
          return {...state, currentSong: state.currentSong+1}
        }
        else {
          return {...state, currentSong: 0}
        }

    }
      default: return state;
  };
}

  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    document.addEventListener("keydown", (event) => {
      console.log(event.key);
      let currSound = state.drumTypes.filter((drum) => drum.key === event.key);
      currSound.map((sound) => {
        sound.source.currentTime = 0;
        sound.source.play();
        return null;
      });
    });
  });

  const drumList = state.drums.map((drum) => (
    <button
      key={drum.type}
      onClick={() => {
        drum.source.currentTime = 0;
        drum.source.play();
      }}
    >
      <img src={drum.img} alt={drum.type}></img>
      <p>{drum.key}</p>
    </button>
  ));

  return (

    <div id="drum-machine">
      <h1>Beat Player</h1>
        <div id="melody">
          <audio controls loop src={state.melodies[state.currentSong].source}></audio>
          <button id="next" onClick={() => dispatch({ type: actions.TOGGLESONG, move: actions.NEXTSONG })}>NEXT</button>
          <button id="prev" onClick={() => dispatch({ type: actions.TOGGLESONG, move: actions.PREVSONG })}>PREV</button>
        </div>

        <div id="drums">{drumList}</div>
        <div id="display"></div>
    </div>
  );
}

export default App;
