import { useEffect, useReducer } from "react";
import "./App.css";


function App() {




  const initialState = {
  drumTypes: [
    {
      type: "kick",
      source: new Audio(require("./sounds/kick.mp3")),
      keyCode: 81 //q https://www.toptal.com/developers/keycode
    },
    {
      type: "clap",
      source: new Audio(require("./sounds/clap.mp3")),
      keyCode: 87 //w
    },
    {
      type: "hi-hat",
      source: new Audio(require("./sounds/hihat.mp3")),
      keyCode: 69 //e
    },
    {
      type: "openhat",
      source: new Audio(require("./sounds/openhat.mp3")),
      keyCode: 82 //r
    },
  ]
};

useEffect(()=>{
  document.addEventListener('keydown', (event) =>{
    console.log(event.key)
    let currSound = initialState.drumTypes.filter((drum)=>drum.event.key)
    currSound.map((sound)=> sound.source.play())
  })
})

  const reducer = () => {};

  const [state, dispatch] = useReducer(reducer, initialState);

  const drumList = initialState.drumTypes.map((drum) => (
    <button key={drum.type} onClick={() => drum.source.play()}>
      {drum.type}
    </button>
  ));

  return (
    <div id="drum-machine">
      <h1>beat player</h1>

      <div id="melody"></div>

      <div id="drums">{drumList}</div>
      <div id="display"></div>
    </div>
  );
}

export default App;
