import { useReducer } from 'react';
import './App.css';

const initialState = {
  drumTypes: [
    {
      type: 'kick',
      source: new Audio("./kick.wav")
    },
    {
      type: 'snare',
      source: new Audio("./snare.wav")
    }
  ]
}

function App() {
  const reducer = () => {}

  const [state, dispatch] = useReducer(reducer, initialState)

  const playDrumSound = (source) => {
    if (source) {
      source.play()
    }
  }

  const drumList = state.drumTypes.map((drum) => (
    <p key={drum.type} onClick={() => playDrumSound(drum.source)}> {drum.type} </p>
  ))

  return (
    <div id="drum-machine">
      <h1>beat player</h1>
      <div id="melody"></div>
      <div id="drums">
        {drumList}
      </div>
      <div id="display"></div>
    </div>
  );
}

export default App;
